add_action('after_setup_theme', function () {
    $version = '1.0.0';
    if (get_option('gostudy_option_version') != $version) {
        gostudy_get_default_option();
        update_option('gostudy_option_version', $version);
    }
});

function gostudy_fonts_url()
{
    $fonts_url = '';

    $font1 = esc_html_x('on', 'Nunito Sans: on or off', 'gostudy');
    $font2 = esc_html_x('on', 'Khula: on or off', 'gostudy');
    $font3 = esc_html_x('on', 'Red Hat Display: on or off', 'gostudy');

    if ('off' !== $font1 || 'off' !== $font2 || 'off' !== $font3) {
        $font_families = [];

        if ('off' !== $font1) {
            $font_families[] = 'Nunito Sans:400,600,700,800';
        }

        if ('off' !== $font2) {
            $font_families[] = 'Khula:600,700,800';
        }

        if ('off' !== $font3) {
            $font_families[] = 'Red Hat Display:700';
        }

        $query_args = [
            'family' => urlencode(implode('|', $font_families)),
            'subset' => urlencode('latin,latin-ext'),
        ];

        $fonts_url = add_query_arg($query_args, 'https://fonts.googleapis.com/css');
    }

    return esc_url_raw($fonts_url);
}

add_action('admin_enqueue_scripts', function () {
    wp_enqueue_style('editor-google-fonts', gostudy_fonts_url(), [], '1.0.0');
});

if (!class_exists('Gostudy_CorePlugin')) {
    add_action('wp_enqueue_scripts', function () {
        wp_enqueue_style('gostudy-default-font', gostudy_fonts_url(), [], '1.0.0');
    });
}
